  addNode(id, data) {
    this.nodes.set(id, { ...data, id });
  }

  // Add an edge (relationship)
  addEdge(from, to, type) {
    this.edges.push({ from, to, type });
  }

  // Get node by id
  getNode(id) {
    return this.nodes.get(id);
  }

  // Get all nodes of a type
